import { Component } from '@angular/core';
import { BankdataService } from 'src/app/services/bankdata.service';

@Component({
  selector: 'app-bank',
  templateUrl: './bank.component.html',
  styleUrls: ['./bank.component.css']
})
export class BankComponent {
  email:any
  accountnumber: any = ""
  bankname: any
  ifsccode: any
  location:any
  type: any
  isLoading: boolean = true;
  isEmpty:boolean= false
  dataarray: any = []
  isEditingCode: boolean = true
  constructor(private user: BankdataService){
   setTimeout(()=>{
    this.email = this.user.useremail
    this.accountnumber = this.user.userbank
    this.isLoading = false;
    this.checkdata()
   },5000)
  }
  ngOnInit(){

  }
  addDetails(){
    var data = {
      account: this.accountnumber,
      bankname: this.bankname,
      ifsccode: this.ifsccode,
      branch: this.location,
      accounttype: this.type
    }
    this.user.addBank(data).subscribe((curr)=>{
      if(curr.success){
        this.accountnumber = "",
        this.bankname = "",
        this.ifsccode = "",
        this.location = "",
        this.type = ""
        this.checkdata()
      }
      else{
        console.log("Error")
      }
    })
  }
  checkdata(){
    this.user.checkdata().subscribe((curr)=>{
      console.log(curr)
      if(curr.success){
        this.isEmpty = true
      }
      else{
        this.dataarray.push(curr.data)
        this.isEmpty = false
      }
    })
  }
  enableEdit(i:any){
    i.isEditingCode = !i.isEditingCode;
    i.isEditingBranch = !i.isEditingBranch;
  }
  editData(id:any){
    var data = {
      "branch": id.branch,
      "ifsccode": id.ifsccode
    }
    id.isEditingCode = !id.isEditingCode;
    id.isEditingBranch = !id.isEditingBranch;
    this.user.editBank(data).subscribe((curr)=>{
     console.log(curr.message)
    })
  }
  deleteedit(){
    var data = {
      "account": "",
       "branch" : "",
       "ifsccode" : "",
       "bankname" : "",
       "accounttype" : ""
    }
    const confirmed = confirm('Do you want to delete data');
    if(confirmed){
      this.user.deleteBank(data).subscribe((curr)=>{
        this.isEmpty = true
      })
    }
    else{
      console.log("Delete")
    }
  }
}
<div *ngIf="isLoading">
    <div class="spinner-border" role="status">
      <span class="sr-only">Loading....</span>
    </div>
  </div>

  <div *ngIf="!isLoading">
    <h4>Welcome, {{ email }} </h4>
    <ng-container *ngIf="isEmpty; else emptynull">
     <div class="nodata">
        <img src="https://i.imgur.com/j4csC2q.png" alt="">
        <button class="btn btn-primary" data-mdb-toggle="modal" data-mdb-target="#exampleModal">Add Details</button>


       <!-- Pop Up -->
  <div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
    <div class="modal-dialog">
      <div class="modal-content">
        <div class="modal-header">
          <h5 class="modal-title" id="exampleModalLabel">Bank Details</h5>
          <button type="button" class="btn-close" data-mdb-dismiss="modal" aria-label="Close"></button>
        </div>
        <div class="modal-body">
          <div class="mb-3">
            <label for="exampleInputEmail1" class="form-label">Bank Account</label>
            <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" [(ngModel)] = "accountnumber">         
           </div>
          <div class="mb-3">
            <label for="exampleInputEmail1" class="form-label">Bank Name</label>
            <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp"  [(ngModel)] = "bankname">         
           </div>
          <div class="mb-3">
            <label for="exampleInputEmail1" class="form-label">IFSC Code</label>
            <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp"  [(ngModel)] = "ifsccode">         
           </div>
           <label for="exampleInputEmail1" class="form-label">Branch Location</label>

           <select class="form-select" aria-label="Default select example" [(ngModel)] = "location">
            <option selected  >Select Location</option>
            <option value="New Delhi">New Delhi</option>
